import React from 'react';
import { Button } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import ArrowUpwardIcon from '@material-ui/icons/ArrowUpward';
import { Link } from "react-scroll";

const useStyles = makeStyles((theme) => ({
    buttonContainerStyles: {
        width: '150px',
        height: '300px',
        margin: '0 auto',
        textAlign: 'center',
    },

    buttonStyles:{
        fontSize: '25px',
        border: 'solid 1px black',
        borderRadius: '15%',
        backgroundColor: 'rgb(228, 130, 74)',
        color: 'black',
        textTransform: 'none',
        marginBottom: '10px',
        '&:hover': {
            marginBottom: '300px',
            border: 'solid 4px rgb(228, 130, 74)',
            background: '#000000'
        }
    },
    arrowStyles:{
        fontSize: '100px',
        marginLeft: '0px',
        color: "rgb(228, 130, 74)"
    }
    
}));

const ScrollUp = () => {
    
    
    const classes =  useStyles();
    return(
        <div className={classes.buttonContainerStyles}>
            <ArrowUpwardIcon className={classes.arrowStyles} />
            <br></br>
            <Link
                        activeClass="active"
                        to="projects"
                        spy={true}
                        smooth={true}
                        offset={-70}
                        duration= {500}
            >
                <Button className={classes.buttonStyles} >See <br></br>Projects</Button>
            </Link>
        </div>
    )
}

export default ScrollUp;
