import React from 'react';
import './components.css';
import { Button } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles'
import ArrowDownwardIcon from '@material-ui/icons/ArrowDownward';


const useStyles = makeStyles((theme) => ({
    buttonContainerStyles: {
        position: 'relative',
        width: '150px',
        height: '300px',
        left: "-38px",
        marginTop: '300px',
        margin: '0 auto',
        textAlign: 'center'
    },

    buttonStyles:{
        fontSize: '25px',
        border: 'solid 1px black',
        borderRadius: '25%',
        backgroundColor: 'black',
        color: 'rgb(228, 130, 74)',
        textTransform: 'none',
        '&:hover': {
            marginBottom: '200px'
        }
    },

    arrowStyles:{
        fontSize: '100px',
        display: 'none'
    }
    
}));

const SeeProjects = () => {
    function handleHover(){
        const visibile = 'visibile'
        classes.arrowStyles.display = visible
    }
    
    const classes =  useStyles();
    return(
        <div className={classes.buttonContainerStyles}>
            <Button className={classes.buttonStyles} onHover={handleHover()}>See <br></br>Projects</Button>
            <br></br>
            <ArrowDownwardIcon className={classes.arrowStyles}/>
        </div>
    )
}

export default SeeProjects;
