import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
const useStyles = makeStyles(() => ({
    description: {
        backgroundColor: 'black',
        width: '90%',
        height: 'auto',
        marginLeft: '40px',
        color: 'rgb(228, 130, 74)',
        fontFamily: 'roboto',
        fontSize: '40px',
    },

    text:{
        textAlign: 'left',
    },

    myName:{
        fontSize:'50px',
        borderBottom: '1px solid rgb(228, 130, 74)'
    },
    
    paragraph:{
    }
    
}));
const Description = () =>{
    const classes = useStyles();
    return (
        <div className={classes.description}>
            <section className={classes.text}>
                <p className={classes.paragraph}>Life Long Learner
                -that's the best way to describe me. Whether it is studying math proofs and algorithms as a college student, crafting a well-thought out lesson as a math teacher, or designing a beautiful and consistent website, I always give it my all.<br></br><br></br>
                I am a self taught developer based out of Colorado who has strong skills in React front-end development. In fact, I designed this whole site in React. Although, I am mostly focused in front-end development, I have experience working with building apis with Node libraries such as Node.express, managing databases using mongoDB. 
                </p>
            </section>
        </div>
    )
}

export default Description;
