import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
const useStyles = makeStyles(() => ({
    description: {
        backgroundColor: 'black',
        width: '90%',
        height: 'auto',
        marginTop: '100px',
        paddingLeft: '40px',
        color: 'rgb(228, 130, 74)',
        fontFamily: 'roboto',
        fontSize: '40px',
        display: 'inline-block'
    },

    myFace:{
        borderRadius: '50%',
        position: 'relative',
        top: '-80px',
        display: 'inline-block'
    },

    myName:{
        fontSize:'50px',
        borderBottom: '1px solid rgb(228, 130, 74)'
    }
    
}));
const Description = () =>{
    const classes = useStyles();
    return (
        <div className={classes.description}>
            <img src={require('./myFace.jpg')} height="175px" align='left' className={classes.myFace}/>
            <header>
            Life long Learner
            </header>
        </div>
    )
}

export default Description;
