export const pageVariant = {
    in: {
        opacity: 1,
        x:"0"
    },
    out: {
        opacity: .5,
        x:'-100vh'
    }
}

export const pageTransition = {
    type: 'tween',
    ease: 'anticipate',
    duration: 1
}

export default { pageVariant, pageTransition }
