import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';

const useStyles = makeStyles({
  root: {
    maxWidth: 345,
    backgroundColor: "black",
    border: 'solid 1px rgb(228, 130, 74)',
    height: 'auto',
    margin: '0 auto',
    '&:hover': {
        borderRadius: '10%',
        border: 'solid 3px rgb(228, 130, 74)'
    }
    
    
  },
  media: {
    height: 140,
  },

  typographyStyles:{
      color: 'rgb(228, 130, 74)'
  },

  links:{
      color: "#396395",
      opacity: '0.9',
      paddingBottom: '30px',
      "&:hover": {
          textDecoration: 'underline',
          opacity: '1.0'
      }
  },
  media:{
    dispaly: 'block'
  }
});

export default function ProjectCard() {
  const classes = useStyles();

  return (
    <Card className={classes.root}>
      <CardActionArea>
        <CardMedia
          className={classes.media}
          image= "../img/mount-hood.jpg"
          title="Contemplative Reptile"
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="h2" className={classes.typographyStyles}>
            Border-Radius Previewer
          </Typography>
          <Typography variant="body2" color="textSecondary" component="p" className={classes.typographyStyles}>
            Lizards are a widespread group of squamate reptiles, with over 6,000 species, ranging
            across all continents except Antarctica
          </Typography>
        </CardContent>
      </CardActionArea>
      <CardActions >
        <Button size="small" color="primary" className={classes.links}>
          Preview
        </Button>
        <Button size="small" color="primary" className={classes.links}>
          Source Code
        </Button>
      </CardActions>
    </Card>
  );
}
